import java.net.InetSocketAddress;
import java.nio.ByteBuffer;
import java.nio.channels.DatagramChannel;
import java.nio.charset.StandardCharsets;

public class MulticastUDP {
    private DatagramChannel channel;
    private InetAddress group;
    private int port;

    public MulticastUDP(String host, int port) throws IOException {
        this.group = InetAddress.getByName(host);
        this.port = port;
        this.channel = DatagramChannel.open();
    }

    public void joinGroup() throws IOException {
        channel.bind(new InetSocketAddress(port));
        channel.join(group, InetAddress.getLocalHost());
        channel.configureBlocking(false);
    }

    public String receive() throws IOException {
        ByteBuffer buffer = ByteBuffer.allocate(1024);
        InetSocketAddress senderAddress = (InetSocketAddress) channel.receive(buffer);
        if (senderAddress != null) {
            buffer.flip();
            byte[] data = new byte[buffer.remaining()];
            buffer.get(data);
            return new String(data, StandardCharsets.UTF_8);
        }
        return null;
    }

    public void send(String message, String targetHost, int targetPort) throws IOException {
        byte[] data = message.getBytes(StandardCharsets.UTF_8);
        ByteBuffer buffer = ByteBuffer.wrap(data);
        channel.send(buffer, new InetSocketAddress(targetHost, targetPort));
    }

    public void close() throws IOException {
        channel.close();
    }
}
