import io.vertx.core.logging.Logger;
import io.vertx.core.logging.LoggerFactory;

public class HAProxy extends AbstractVerticle {

    private final Logger log = LoggerFactory.getLogger(HAProxy.class);
    private static final String HOST = "localhost";
    private static final int PORT = 11080;
    private final SocketAddress remoteAddress;
    private final Buffer header;
    private NetServer server;
    private NetClient client;
    private SocketAddress connectionRemoteAddress;
    private SocketAddress connectionLocalAddress;

    public HAProxy(SocketAddress remoteAddress, Buffer header) {
        this.remoteAddress = remoteAddress;
        this.header = header;
    }

    @Override
    public void start(Future<Void> startFuture) throws Exception {
        NetServerOptions options = new NetServerOptions();
        server = vertx.createNetServer(options);
        client = vertx.createNetClient(new NetClientOptions());

        server.connectHandler(socket -> {
            client.connect(remoteAddress.port(), remoteAddress.host(), ar -> {
                if (ar.succeeded()) {
                    connectionRemoteAddress = ar.result().remoteAddress();
                    connectionLocalAddress = ar.result().localAddress();
                    ar.result().write(header);
                    socket.pipeTo(ar.result());
                    ar.result().pipeTo(socket);
                } else {
                    log.error("Failed to connect to remote server", ar.cause());
                    socket.close();
                }
            });
        });

        server.listen(PORT, HOST, res -> {
            if (res.succeeded()) {
                log.info("HAProxy started on {}:{}", HOST, PORT);
                startFuture.complete();
            } else {
                log.error("Failed to start HAProxy", res.cause());
                startFuture.fail(res.cause());
            }
        });
    }

    @Override
    public void stop(Future<Void> stopFuture) throws Exception {
        if (server != null) {
            server.close();
        }
        if (client != null) {
            client.close();
        }
        stopFuture.complete();
    }

    public SocketAddress getConnectionRemoteAddress() {
        return connectionRemoteAddress;
    }

    public SocketAddress getConnectionLocalAddress() {
        return connectionLocalAddress;
    }

    public static void main(String[] args) {
        Vertx vertx = Vertx.vertx();
        SocketAddress remoteAddress = SocketAddress.inetSocketAddress(8080, "remotehost");
        Buffer header = Buffer.buffer("Custom-Header: Value\r\n\r\n");
        HAProxy proxy = new HAProxy(remoteAddress, header);

        vertx.deployVerticle(proxy, res -> {
            if (res.succeeded()) {
                System.out.println("HAProxy deployed successfully");
            } else {
                System.err.println("Failed to deploy HAProxy: " + res.cause());
            }
        });
    }
}
