import java.time.Duration;
import java.util.concurrent.CompletableFuture;

public class RdioScannerHttpClient {

    private final HttpClient mHttpClient;

    public RdioScannerHttpClient() {
        mHttpClient = HttpClient.newBuilder()
                .version(HttpClient.Version.HTTP_2)
                .connectTimeout(Duration.ofSeconds(20))
                .followRedirects(HttpClient.Redirect.NORMAL)
                .build();
    }

    public String testConnection(RdioScannerConfiguration configuration) {
        HttpRequest request = HttpRequest.newBuilder()
                .uri(URI.create(configuration.getApiUrl() + "/test"))
                .header("Authorization", "Bearer " + configuration.getApiKey())
                .header("System-ID", configuration.getSystemId())
                .GET()
                .build();

        try {
            HttpResponse<String> response = mHttpClient.send(request, HttpResponse.BodyHandlers.ofString());
            return response.statusCode() + ": " + response.body();
        } catch (Exception e) {
            return "Error: " + e.getMessage();
        }
    }

    public void sendAudioRecordingAsync(RdioScannerConfiguration config, RdioScannerBuilder bodyBuilder) {
        HttpRequest request = HttpRequest.newBuilder()
                .uri(URI.create(config.getApiUrl() + "/upload"))
                .header("Authorization", "Bearer " + config.getApiKey())
                .header("System-ID", config.getSystemId())
                .POST(bodyBuilder.buildMultipartBody())
                .build();

        CompletableFuture<HttpResponse<String>> responseFuture = mHttpClient.sendAsync(request, HttpResponse.BodyHandlers.ofString());

        responseFuture.thenAccept(response -> {
            if (response.statusCode() == 200) {
                System.out.println("Audio recording uploaded successfully");
