import org.apache.kafka.clients.admin.*;
import org.apache.kafka.common.config.Config;
import org.apache.kafka.common.config.ConfigEntry;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import java.time.Duration;
import java.util.*;
import java.util.concurrent.ExecutionException;

public class KafkaAdminTopicConfigProvider {
    private final Properties _clusterConfigs;
    private final AdminClient _adminClient;
    private static final Duration DESCRIBE_CLUSTER_CONFIGS_TIMEOUT = Duration.ofSeconds(90);
    private static final Logger LOG = LoggerFactory.getLogger(KafkaAdminTopicConfigProvider.class);

    public KafkaAdminTopicConfigProvider(Properties clusterConfigs) {
        this._clusterConfigs = clusterConfigs;
        this._adminClient = AdminClient.create(clusterConfigs);
    }

    public Properties topicConfigs(String topic) {
        try {
            DescribeConfigsResult result = _adminClient.describeConfigs(Collections.singleton(new ConfigResource(ConfigResource.Type.TOPIC, topic)));
            Config config = result.all().get().get(new ConfigResource(ConfigResource.Type.TOPIC, topic));
            return convertConfigToProperties(config);
        } catch (InterruptedException | ExecutionException e) {
            LOG.warn("Failed to retrieve configuration for topic {}", topic, e);
            return new Properties();
        }
    }

    public Map<String, Properties> topicConfigs(Set<String> topics) {
        Map<ConfigResource, Config> configs;
        try {
            Set<ConfigResource> resources = topics.stream()
                    .map(topic -> new ConfigResource(ConfigResource.Type.TOPIC, topic))
                    .collect(Collectors.toSet());
            DescribeConfigsResult result = _adminClient.describeConfigs(resources);
            configs = result.all().get();
        } catch (InterruptedException | ExecutionException e) {
            LOG.warn("Failed to retrieve configurations for topics {}", topics, e);
            return new HashMap<>();
        }

        Map<String, Properties> topicConfigs = new HashMap<>();
        for (Map.Entry<ConfigResource, Config> entry : configs.entrySet()) {
            topicConfigs.put(entry.getKey().name(), convertConfigToProperties(entry.getValue()));
        }
        return topicConfigs;
    }

    public Map<String, Properties> allTopicConfigs() {
        try {
            ListTopicsOptions options = new ListTopicsOptions().listInternal(true);
            Set<String> topics = _adminClient.listTopics(options).names().get();
            return topicConfigs(topics);
        } catch (InterruptedException | ExecutionException e) {
            LOG.warn("Failed to retrieve all topic configurations", e);
            return new HashMap<>();
        }
    }

    private Properties convertConfigToProperties(Config config) {
        Properties properties = new Properties();
        for (ConfigEntry entry : config.entries()) {
            if (entry.value() != null) {
                properties.setProperty(entry.name(), entry.value());
            }
        }
        return properties;
    }
}
