    public MilkmanWebsocketClient(URI serverUri, Subscriber<byte[]> responseSubscriber, AsyncControl asyncControl) {
        super(serverUri);
        this.responseSubscriber = responseSubscriber;
        this.asyncControl = asyncControl;
    }

    @Override
    public void onOpen(ServerHandshake handshakedata) {
        // Trigger request ready
        asyncControl.requestReady();
    }

    @Override
    public void send(String message) {
        super.send(message);
        // Notify subscriber of sent message
        responseSubscriber.onNext(message.getBytes());
    }

    @Override
    public void onMessage(String message) {
        // Notify subscriber of received message
        responseSubscriber.onNext(message.getBytes());
    }

    @Override
    public void onClose(int code, String reason, boolean remote) {
        // Complete subscriber and mark request success
        responseSubscriber.onComplete();
        asyncControl.requestSuccess();
    }

    @Override
    public void onError(Exception ex) {
        // Notify subscriber and mark request failure
        responseSubscriber.onError(ex);
        asyncControl.requestFailure();
    }
}
