import java.net.InetSocketAddress;
import java.nio.ByteBuffer;
import java.util.concurrent.CopyOnWriteArrayList;

public class ChatServer extends WebSocketServer {

    private CopyOnWriteArrayList<WebSocket> connections = new CopyOnWriteArrayList<>();

    public ChatServer(int port) {
        super(port);
    }

    public ChatServer(InetSocketAddress address) {
        super(address);
    }

    public ChatServer(int port, Draft_6455 draft) {
        super(port, draft);
    }

    @Override
    public void onOpen(WebSocket conn, ClientHandshake handshake) {
        connections.add(conn);
        conn.send("Welcome to the chat!");
        broadcast("New user joined: " + conn.getRemoteSocketAddress());
    }

    @Override
    public void onClose(WebSocket conn, int code, String reason, boolean remote) {
        connections.remove(conn);
        broadcast("User left: " + conn.getRemoteSocketAddress());
    }

    @Override
    public void onMessage(WebSocket conn, String message) {
        broadcast(message, conn);
    }

    @Override
    public void onMessage(WebSocket conn, ByteBuffer message) {
        // Handle binary message if needed
    }

    @Override
    public void onError(WebSocket conn, Exception ex) {
        ex.printStackTrace();
    }

    @Override
    public void onStart() {
        System.out.println("ChatServer started on port " + getPort());
    }

    private void broadcast(String message, WebSocket exclude) {
        for (WebSocket conn : connections) {
            if (conn != exclude) {
                conn.send(message);
            }
        }
    }

    private void broadcast(String message) {
        for (WebSocket conn : connections) {
            conn.send(message);
        }
    }
}
