        clients.decrementAndGet();
        broadcast(conn.getResourceDescriptor() + " has left the chat.");
        System.out.println("Connection closed by " + conn.getRemoteSocketAddress());
        if (clients.get() == 0) {
            System.out.println("No more clients connected, shutting down server.");
            stop();
        }
    }

