    public void onMessage(WebSocket conn, ByteBuffer message) {
        byte[] bytes = new byte[message.remaining()];
        message.get(bytes);
        broadcast(bytes);
        System.out.println("Received binary message from " + conn.getResourceDescriptor() + ": " + bytes.length + " bytes");
    }

