import org.apache.hugegraph.pd.grpc.discovery.NodeInfos;
import org.apache.hugegraph.pd.grpc.discovery.Query;
import org.apache.hugegraph.pd.grpc.discovery.RegisterInfo;
import io.grpc.ManagedChannel;
import io.grpc.ManagedChannelBuilder;
import java.util.concurrent.TimeUnit;
import java.util.concurrent.atomic.AtomicBoolean;
import java.util.concurrent.locks.ReentrantReadWriteLock;
import java.util.function.Function;

public class DiscoveryClient {

    private final AtomicBoolean requireResetStub = new AtomicBoolean(false);
    private ManagedChannel channel = null;
    private DiscoveryServiceGrpc.DiscoveryServiceBlockingStub registerStub;
    private DiscoveryServiceGrpc.DiscoveryServiceBlockingStub blockingStub;
    private ReentrantReadWriteLock readWriteLock = new ReentrantReadWriteLock();

    public DiscoveryClient(String centerAddress) {
        resetChannel(centerAddress);
    }

    public NodeInfos getNodeInfos(Query query) {
        return tryWithTimes((q) -> {
            this.readWriteLock.readLock().lock();
            NodeInfos nodes;
            try {
                nodes = this.blockingStub.getNodes(q);
            } finally {
                this.readWriteLock.readLock().unlock();
            }
            return nodes;
        }, query);
    }

    private <V, R> R tryWithTimes(Function<V, R> function, V v) {
        R r;
        Exception ex = null;
        for (int i = 0; i < 3; i++) {
            try {
                r = function.apply(v);
                return r;
            } catch (Exception e) {
                requireResetStub.set(true);
                resetStub();
                ex = e;
            }
        }
        if (ex != null) {
            throw new RuntimeException("Discovery method failed", ex);
        }
        return null;
    }

    private void resetStub() {
        if (requireResetStub.get()) {
            try {
                resetChannel(pdAddresses.get(0));
            } catch (Exception e) {
                throw new RuntimeException("Failed to reset stub", e);
            }
        }
    }

    private void resetChannel(String address) {
        readWriteLock.writeLock().lock();
        try {
            if (requireResetStub.get()) {
                if (channel != null) {
                    channel.shutdownNow();
                    channel.awaitTermination(100, TimeUnit.MILLISECONDS);
                }
                channel = ManagedChannelBuilder.forTarget(address)
                        .usePlaintext().build();
                this.registerStub = DiscoveryServiceGrpc.newBlockingStub(channel);
                this.blockingStub = DiscoveryServiceGrpc.newBlockingStub(channel);
                requireResetStub.set(false);
            }
        } catch (Exception e) {
            throw new RuntimeException("Failed to reset channel", e);
        } finally {
            readWriteLock.writeLock().unlock();
        }
    }

    public void close() {
        readWriteLock.writeLock().lock();
        try {
            if (channel != null) {
                channel.shutdownNow();
                channel.awaitTermination(100, TimeUnit.MILLISECONDS);
            }
        } catch (Exception e) {
            throw new RuntimeException("Failed to close channel", e);
        } finally {
            readWriteLock.writeLock().unlock();
        }
    }
}

