                    .maxMessageSize(DEFAULT_MAX_MESSAGE_SIZE)
                    .addServices(grpcServiceBeanMap.values())
                    .build()
                    .start();
            log.info("gRPC server started, listening on {}", grpcServerPort);
            Runtime.getRuntime().addShutdownHook(new Thread(() -> {
                log.warn("*** shutting down gRPC server since JVM is shutting down");
                grpcStop();
                log.warn("*** server shut down");
            }));
        } catch (IOException e) {
            log.error("Failed to start gRPC server", e);
        }
    }

    @PreDestroy
    public void grpcStop() {
        if (server != null) {
            server.shutdown();
            try {
                if (!server.awaitTermination(60, java.util.concurrent.TimeUnit.SECONDS)) {
                    server.shutdownNow();
                }
            } catch (InterruptedException e) {
                server.shutdownNow();
            }
            log.info("gRPC server stopped");
        }
    }
}
