import java.net.HttpURLConnection;
import java.net.URL;
import org.bouncycastle.cert.ocsp.OCSPException;
import org.bouncycastle.cert.ocsp.OCSPReq;
import org.bouncycastle.cert.ocsp.OCSPResp;

public class OCSPClient {

    public static OCSPResp sendOCSPRequest(String url, OCSPReq ocspReq) throws IOException, OCSPException {
        HttpURLConnection connection = null;
        InputStream inputStream = null;
        OutputStream outputStream = null;
        OCSPResp ocspResp = null;

        try {
            URL ocspUrl = new URL(url);
            connection = (HttpURLConnection) ocspUrl.openConnection();
            connection.setRequestMethod("POST");
            connection.setDoOutput(true);

            outputStream = connection.getOutputStream();
            outputStream.write(ocspReq.getEncoded());
            outputStream.flush();

            int responseCode = connection.getResponseCode();
            if (responseCode != HttpURLConnection.HTTP_OK) {
                throw new IOException("Invalid response code: " + responseCode);
            }

            inputStream = connection.getInputStream();
            byte[] responseData = new byte[1024];
            int bytesRead;
            ByteArrayOutputStream byteArrayOutputStream = new ByteArrayOutputStream();
            while ((bytesRead = inputStream.read(responseData)) != -1) {
                byteArrayOutputStream.write(responseData, 0, bytesRead);
            }

            ocspResp = new OCSPResp(byteArrayOutputStream.toByteArray());

        } finally {
            if (outputStream != null) {
                outputStream.close();
            }
            if (inputStream != null) {
                inputStream.close();
            }
            if (connection != null) {
                connection.disconnect();
            }
        }

        return ocspResp;
    }
}
