import java.io.*;
import java.net.*;
import java.util.logging.*;

public class TcpTransport {
    private ServerSocket srv_sock;
    private Socket sock;
    private OutputStream output;
    private InputStream input;
    private Receiver receiver_thread;
    private RtReceiver receiver;
    private InetAddress host;
    private int port = 7800;
    private int out_buf_size = 8192;
    private int in_buf_size = 8192;
    private boolean server = false;
    private boolean tcp_nodelay = false;
    private Logger log = Logger.getLogger(TcpTransport.class.getName());

    public void start(String... options) throws IOException {
        for (String option : options) {
            if (option.equals("server")) {
                server = true;
            } else if (option.startsWith("host=")) {
                host = InetAddress.getByName(option.substring(5));
            } else if (option.startsWith("port=")) {
                port = Integer.parseInt(option.substring(5));
            } else if (option.startsWith("out_buf_size=")) {
                out_buf_size = Integer.parseInt(option.substring(13));
            } else if (option.startsWith("in_buf_size=")) {
                in_buf_size = Integer.parseInt(option.substring(12));
            } else if (option.equals("tcp_nodelay")) {
                tcp_nodelay = true;
            }
        }

        if (server) {
            srv_sock = new ServerSocket(port);
            sock = srv_sock.accept();
        } else {
            sock = new Socket(host, port);
        }

        sock.setTcpNoDelay(tcp_nodelay);
        output = sock.getOutputStream();
        input = sock.getInputStream();

        receiver_thread = new Receiver(input);
        receiver_thread.start();
    }

    public void stop() throws IOException {
        if (receiver_thread != null) {
            receiver_thread.interrupt();
        }
        if (output != null) {
            output.close();
        }
        if (input != null) {
            input.close();
        }
        if (sock != null) {
            sock.close();
        }
        if (srv_sock != null) {
            srv_sock.close();
        }
    }

    public void send(byte[] buf, int offset, int length) throws IOException {
        output.write(buf, offset, length);
        output.flush();
    }

    private class Receiver extends Thread {
        private InputStream in;

        public Receiver(InputStream in) {
            this.in = in;
        }

        @Override
        public void run() {
            byte[] buffer = new byte[in_buf_size];
            try {
                while (!isInterrupted()) {
                    int bytesRead = in.read(buffer);
                    if (bytesRead > 0 && receiver != null) {
                        receiver.onDataReceived(buffer, bytesRead);
                    }
                }
            } catch (IOException e) {
                log.log(Level.SEVERE, "Error reading from socket", e);
            }
        }
    }

    public interface RtReceiver {
        void onDataReceived(byte[] data, int length);
    }
}
