import java.util.ArrayList;
import java.util.List;
import java.util.concurrent.atomic.AtomicBoolean;

public class EchoServer extends Thread {
    private final int port;
    private ServerSocket serverSocket;
    private final List<Thread> threads = new ArrayList<>();
    private final List<Socket> sockets = new ArrayList<>();
    private final List<Exception> exceptions = new ArrayList<>();
    private final AtomicBoolean renegotiate = new AtomicBoolean(false);

    public EchoServer(int port) {
        this.port = port;
    }

    public EchoServer(SSLFactory sslFactory, int port) {
        try {
            this.serverSocket = sslFactory.createSSLServerSocket(port);
        } catch (IOException e) {
            exceptions.add(e);
        }
        this.port = port;
    }

    public void renegotiate() {
        renegotiate.set(true);
    }

    @Override
    public void run() {
        try {
            if (serverSocket == null) {
                serverSocket = new ServerSocket(port);
            }
            while (!isInterrupted()) {
                Socket clientSocket = serverSocket.accept();
                sockets.add(clientSocket);
                Thread clientHandler = new ClientHandler(clientSocket);
                threads.add(clientHandler);
                clientHandler.start();
            }
        } catch (IOException e) {
            exceptions.add(e);
        } finally {
            close();
        }
    }

    public void closeConnections() {
        for (Socket socket : sockets) {
            try {
                socket.close();
            } catch (IOException e) {
                exceptions.add(e);
            }
        }
    }

    public void close() {
        closeConnections();
        try {
            if (serverSocket != null && !serverSocket.isClosed()) {
                serverSocket.close();
            }
        } catch (IOException e) {
            exceptions.add(e);
        }
        interrupt();
        for (Thread thread : threads) {
            try {
                thread.join();
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
            }
        }
    }

    public int getExceptionCount() {
        return exceptions.size();
    }

    private class ClientHandler extends Thread {
        private final Socket socket;

        public ClientHandler(Socket socket) {
            this.socket = socket;
        }

        @Override
        public void run() {
            try (InputStream input = socket.getInputStream();
                 OutputStream output = socket.getOutputStream()) {

                byte[] buffer = new byte[1024];
                int bytesRead;
                while ((bytesRead = input.read(buffer)) != -1) {
                    output.write(buffer, 0, bytesRead);
                    output.flush();
                    if (renegotiate.getAndSet(false)) {
                        SSLSocket sslSocket = (SSLSocket) socket;
                        sslSocket.startHandshake();
                    }
                }
            } catch (IOException e) {
                exceptions.add(e);
            } finally {
                try {
                    socket.close();
                } catch (IOException e) {
                    exceptions.add(e);
                }
            }
        }
    }

    public static class SSLFactory {
        private final SSLContext sslContext;

        public SSLFactory(String keyStorePath, String keyStorePassword) throws IOException, GeneralSecurityException {
            KeyStore keyStore = KeyStore.getInstance("JKS");
            try (FileInputStream keyStoreFile = new FileInputStream(keyStorePath)) {
                keyStore.load(keyStoreFile, keyStorePassword.toCharArray());
            }
            KeyManagerFactory keyManagerFactory = KeyManagerFactory.getInstance(KeyManagerFactory.getDefaultAlgorithm());
            keyManagerFactory.init(keyStore, keyStorePassword.toCharArray());

            sslContext = SSLContext.getInstance("TLS");
            sslContext.init(keyManagerFactory.getKeyManagers(), null, null);
        }

        public SSLServerSocket createSSLServerSocket(int port) throws IOException {
            SSLServerSocketFactory sslServerSocketFactory = sslContext.getServerSocketFactory();
            SSLServerSocket sslServerSocket = (SSLServerSocket) sslServerSocketFactory.createServerSocket(port);
            sslServerSocket.setEnabledProtocols(new String[]{"TLSv1.2", "TLSv1.3"});
            return sslServerSocket;
        }
    }
}
