        }

        // Generate handshake response
        String acceptKey = generateAcceptKey(key);
        String response = "HTTP/1.1 101 Switching Protocols\r\n" +
                          "Upgrade: websocket\r\n" +
                          "Connection: Upgrade\r\n" +
                          "Sec-WebSocket-Accept: " + acceptKey + "\r\n\r\n";

        writer.print(response);
        writer.flush();

        // Send binary frames
        byte[] data = { 0x00, 0x01, 0x02, 0x03 }; // Example binary data
        boolean mask = true; // Configurable masking
        sendBinaryFrame(writer, data, mask);

        // Close connection
        clientSocket.close();
    }

    private String generateAcceptKey(String key) {
        try {
            String guid = "258EAFA5-E914-47DA-95CA-C5AB0DC85B11";
            String combined = key + guid;
            byte[] sha1 = combined.getBytes("UTF-8");
            MessageDigest md = MessageDigest.getInstance("SHA-1");
            byte[] hash = md.digest(sha1);
            return Base64.getEncoder().encodeToString(hash);
        } catch (Exception e) {
            e.printStackTrace();
            return "";
        }
    }

    private void sendBinaryFrame(PrintWriter writer, byte[] data, boolean mask) {
        ByteBuffer buffer = ByteBuffer.allocate(10 + data.length);
        buffer.put((byte) 0x82); // FIN bit set, opcode for binary frame

        int payloadLength = data.length;
        if (mask) {
            payloadLength |= 0x80;
        }
        buffer.put((byte) payloadLength);

        if (data.length <= 125) {
            // No extended payload length
        } else if (data.length <= 65535) {
            buffer.putShort((short) data.length);
        } else {
            buffer.putInt(data.length);
        }

        if (mask) {
            byte[] maskingKey = { 0x00, 0x00, 0x00, 0x00 }; // Example masking key
            buffer.put(maskingKey);
            for (int i = 0; i < data.length; i++) {
                data[i] = (byte) (data[i] ^ maskingKey[i % 4]);
            }
        }

        buffer.put(data);
        writer.write(Arrays.toString(buffer.array()));
        writer.flush();
    }

    public void stop() throws IOException {
        if (serverSocket != null && !serverSocket.isClosed()) {
            serverSocket.close();
            System.out.println("WebSocket server stopped");
        }
    }

    public static void main(String[] args) {
        try {
            WebSocketServer server = new WebSocketServer(8080);
            server.start();
            server.handleConnection();
            server.stop();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }
}
