import java.io.*;
import java.net.*;

public class ConnectionManipulationProxyServer implements Runnable {
    private int localPort;
    private String host;
    private int remotePort;
    private Thread proxyThread;
    private final Object enableLock = new Object();
    private boolean enableProxy = false;
    private volatile boolean running = false;
    private Socket client;
    private Socket server;
    private ServerSocket serverSocket;

    public ConnectionManipulationProxyServer(String host, int remotePort, int localPort) {
        this.host = host;
        this.remotePort = remotePort;
        this.localPort = localPort;
    }

    public void startProxy() {
        synchronized (enableLock) {
            if (!enableProxy) {
                enableProxy = true;
                running = true;
                proxyThread = new Thread(this);
                proxyThread.start();
            }
        }
    }

    public void stopProxy() {
        synchronized (enableLock) {
            if (enableProxy) {
                enableProxy = false;
                running = false;
                proxyThread.interrupt();
            }
        }
        killOpenSockets();
    }

    public void killOpenSockets() {
        try {
            if (client != null) client.close();
            if (server != null) server.close();
            if (serverSocket != null) serverSocket.close();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    @Override
    public void run() {
        try {
            serverSocket = new ServerSocket(localPort);
            while (running) {
                client = serverSocket.accept();
                server = new Socket(host, remotePort);
                new Thread(new Forwarder(client.getInputStream(), server.getOutputStream())).start();
                new Thread(new Forwarder(server.getInputStream(), client.getOutputStream())).start();
            }
        } catch (IOException e) {
            if (running) e.printStackTrace();
        } finally {
            killOpenSockets();
        }
    }

    public int getLocalPort() {
        return localPort;
    }

    private static class Forwarder implements Runnable {
        private InputStream in;
        private OutputStream out;

        public Forwarder(InputStream in, OutputStream out) {
            this.in = in;
            this.out = out;
        }

        @Override
        public void run() {
            byte[] buffer = new byte[1024];
            int length;
            try {
