                proxyThread.start();
            }
        }
    }

    public void stopProxy() {
        synchronized (enableLock) {
            if (enableProxy) {
                enableProxy = false;
                running = false;
                proxyThread.interrupt();
                killOpenSockets();
            }
        }
    }

    public void killOpenSockets() {
        try {
            if (client != null && !client.isClosed()) {
                client.close();
            }
            if (server != null && !server.isClosed()) {
