    public void run() {
        try {
            serverSocket = new ServerSocket(localPort);
            while (running) {
                client = serverSocket.accept();
                if (enableProxy) {
                    server = new Socket(host, remotePort);
                    new Thread(new Forwarder(client.getInputStream(), server.getOutputStream())).start();
                    new Thread(new Forwarder(server.getInputStream(), client.getOutputStream())).start();
                } else {
                    client.close();
                }
            }
        } catch (IOException e) {
            if (running) {
                e.printStackTrace();
            }
        } finally {
            killOpenSockets();
        }
    }

    public int getLocalPort() {
        return localPort;
    }

    private static class Forwarder implements Runnable {
        private final InputStream input;
        private final OutputStream output;

        public Forwarder(InputStream input, OutputStream output) {
            this.input = input;
            this.output = output;
        }

        @Override
        public void run() {
            try {
                byte[] buffer = new byte[1024];
                int bytesRead;
