public abstract class NanoHTTPD {
    private String hostname;
    private int myPort;
    private ServerSocket myServerSocket;
    private Thread myThread;

    public NanoHTTPD(int port) {
        this(null, port);
    }

    public NanoHTTPD(String hostname, int port) {
        this.hostname = hostname;
        this.myPort = port;
    }

    public void start() throws Exception {
        myServerSocket = new ServerSocket(myPort, 50, hostname != null ? java.net.InetAddress.getByName(hostname) : null);
        myThread = new Thread(() -> {
            try {
                while (!myServerSocket.isClosed()) {
                    Socket socket = myServerSocket.accept();
                    InputStream inputStream = socket.getInputStream();
                    OutputStream outputStream = socket.getOutputStream();
                    handleConnection(inputStream, outputStream);
                    socket.close();
                }
            } catch (Exception e) {
                if (!myServerSocket.isClosed()) {
                    throw new RuntimeException("Server error", e);
                }
            }
        });
        myThread.setDaemon(true);
        myThread.start();
    }

    public void stop() throws Exception {
        if (myServerSocket != null && !myServerSocket.isClosed()) {
            myServerSocket.close();
        }
        if (myThread != null && myThread.isAlive()) {
            myThread.join();
        }
    }

    protected abstract void handleConnection(InputStream inputStream, OutputStream outputStream) throws Exception;
}
