class InnerSocketThread extends Thread {
    private ServerSocket socket;

    public InnerSocketThread() throws IOException {
        this.socket = new ServerSocket(Manager.PORT);
    }

    @Override
    public void run() {
        try {
            while (true) {
                socket.setSoTimeout(5000);
                Socket clientSocket = socket.accept();
                InputStream in = clientSocket.getInputStream();
                OutputStream os = clientSocket.getOutputStream();

                String command = read(in);
                processCommand(command, os);

                os.close();
                in.close();
                clientSocket.close();
            }
        } catch (IOException e) {
            e.printStackTrace();
        } finally {
            try {
                if (socket != null && !socket.isClosed()) {
                    socket.close();
                }
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    private String read(InputStream in) throws IOException {
        BufferedReader reader = new BufferedReader(new InputStreamReader(in));
        StringBuilder sb = new StringBuilder();
        int c;
        while ((c = reader.read()) != -1 && c != '\r') {
            sb.append((char) c);
        }
        return sb.toString();
    }

    private void write(OutputStream os) throws IOException {
        PrintWriter writer = new PrintWriter(os, true);
        writer.print(Manager.switchFlag ? "running" : "stop");
        writer.println();
    }

    private void processCommand(String command, OutputStream os) throws IOException {
        switch (command) {
            case "START":
                Manager.startProcess();
                break;
            case "STATUS":
                write(os);
                break;
            case "FLUSHMETHOD":
                Manager.flushMethod();
                break;
            default:
                System.out.println("Unknown command: " + command);
        }
    }
}
