import java.io.*;
import java.net.*;

public class SnapshotService implements Runnable {
    private int port;
    private ServerSocket serverSocket;
    private boolean closed = false;

    public SnapshotService(int port) {
        if (port < 0 || port > 65535) {
            throw new IllegalArgumentException("Invalid port number");
        }
        this.port = port;
    }

    public void listen() {
        try {
            int actualPort = openSocket(port);
            System.out.println("Server listening on port " + actualPort);
        } catch (IOException e) {
            System.err.println("Error starting server: " + e.getMessage());
        }
    }

    private int openSocket(int port) throws IOException {
        serverSocket = new ServerSocket(port);
        Thread serverThread = new Thread(this);
        serverThread.start();
        return serverSocket.getLocalPort();
    }

    @Override
    public void run() {
        while (!closed) {
            try {
                Socket socket = serverSocket.accept();
                process(socket);
            } catch (IOException e) {
                if (!isIgnoredException()) {
                    System.err.println("Error accepting connection: " + e.getMessage());
                }
            }
        }
    }

    private boolean isIgnoredException() {
