                        }
                        sb.setLength(0);
                    } else {
                        sb.append((char) l);
                    }
                }
                int contentLength = 0;
                String str = headers.get("Content-Length");
                if (str != null) {
                    contentLength = Integer.parseInt(str);
                }
                OutputStream os = conn.getOutputStream();
                str = headers.get("Expect");
                if ("100-Continue".equalsIgnoreCase(str)) {
                    os.write("HTTP/1.1 100 Continue\r\n\r\n".getBytes("ASCII"));
                    os.flush();
                }
                InputStream input = new BodyInputStream(ins, contentLength);
                OutputStream response = new ResponseOutputStream(os);

                Map<String, String> queries = parsePath(path);
                handle(method, path, headers, queries, input, response);
                response.close();

                conn.close();
            } catch (Exception e) {
                if (conn != null) {
                    try {
                        conn.close();
                    } catch (Exception ee) {}
                }
            }

            if (!ss.isBound() || ss.isClosed()) {
                serverSocket = null;
            }
