public abstract class EmbedHttpServer implements Runnable {
    private int port;
    private ServerSocket serverSocket;
    private volatile boolean running = false;

    public EmbedHttpServer(int port) {
        this.port = port;
    }

    public void start() throws IOException {
        serverSocket = new ServerSocket(port);
        running = true;
        Thread serverThread = new Thread(this);
        serverThread.start();
    }

    public void stop() {
        running = false;
        try {
            if (serverSocket != null) {
                serverSocket.close();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    @Override
    public void run() {
        while (running) {
            try {
                Socket clientSocket = serverSocket.accept();
                handleConnection(clientSocket);
            } catch (IOException e) {
                if (running) {
                    e.printStackTrace();
                }
            }
        }
    }

    private void handleConnection(Socket clientSocket) {
        try (
            InputStream input = clientSocket.getInputStream();
            OutputStream output = clientSocket.getOutputStream();
            BufferedReader reader = new BufferedReader(new InputStreamReader(input));
            PrintWriter writer = new PrintWriter(output, true)
        ) {
            String requestLine = reader.readLine();
            if (requestLine == null) return;

            String[] requestParts = requestLine.split(" ");
            if (requestParts.length < 3) return;

            String method = requestParts[0];
            String path = requestParts[1];

            Map<String, String> headers = new HashMap<>();
            String line;
            while (!(line = reader.readLine()).isEmpty()) {
                String[] headerParts = line.split(": ", 2);
                if (headerParts.length == 2) {
                    headers.put(headerParts[0], headerParts[1]);
                }
            }

            Map<String, String> queries = parsePath(path);

            handle(method, path, headers, queries, input, output);
        } catch (IOException e) {
            e.printStackTrace();
        } finally {
            try {
                clientSocket.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    public abstract void handle(String method, String path, Map<String, String> headers, Map<String, String> queries, InputStream input, OutputStream response);

    private Map<String, String> parsePath(String path) {
        Map<String, String> queries = new HashMap<>();
        int queryIndex = path.indexOf('?');
        if (queryIndex >= 0) {
            String queryString = path.substring(queryIndex + 1);
            String[] pairs = queryString.split("&");
            for (String pair : pairs) {
                int equalIndex = pair.indexOf('=');
                if (equalIndex > 0 && equalIndex < pair.length() - 1) {
                    String key = URLDecoder.decode(pair.substring(0, equalIndex), "UTF-8");
                    String value = URLDecoder.decode(pair.substring(equalIndex + 1), "UTF-8");
                    queries.put(key, value);
                }
            }
        }
        return queries;
    }
}
