import java.io.*;
import java.net.*;
import java.util.HashMap;

public class EmbedHttpServer implements Runnable {
    private int port;
    private ServerSocket serverSocket;
    private boolean running = false;

    public EmbedHttpServer(int port) {
        this.port = port;
    }

    public void start() throws IOException {
        serverSocket = new ServerSocket(port);
        running = true;
        new Thread(this).start();
    }

    public void stop() {
        running = false;
        try {
            if (serverSocket != null) {
                serverSocket.close();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public void handle(String method, String path, HashMap<String, String> headers, InputStream input, OutputStream response) {
        // To be implemented by subclasses
    }

    @Override
    public void run() {
        while (running) {
            try {
                Socket clientSocket = serverSocket.accept();
                BufferedReader reader = new BufferedReader(new InputStreamReader(clientSocket.getInputStream()));
                OutputStream responseStream = clientSocket.getOutputStream();

                String requestLine = reader.readLine();
                if (requestLine != null) {
                    String[] requestParts = requestLine.split(" ");
                    String method = requestParts[0];
                    String path = requestParts[1];

                    HashMap<String, String> headers = new HashMap<>();
                    String line;
                    while ((line = reader.readLine()) != null && !line.isEmpty()) {
                        String[] headerParts = line.split(": ", 2);
                        if (headerParts.length == 2) {
                            headers.put(headerParts[0], headerParts[1]);
                        }
                    }

                    int contentLength = Integer.parseInt(headers.getOrDefault("Content-Length", "0"));
                    InputStream bodyStream = new BodyInputStream(clientSocket.getInputStream(), contentLength);

                    handle(method, path, headers, bodyStream, responseStream);
                }

                responseStream.close();
                reader.close();
                clientSocket.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    private static class BodyInputStream extends InputStream {
        private final InputStream ins;
        private int limit;
        private int bytesRead;

        public BodyInputStream(InputStream ins, int n) {
            this.ins = ins;
            this.limit = n;
            this.bytesRead = 0;
        }

        @Override
        public int available() throws IOException {
            return Math.min(limit - bytesRead, ins.available());
        }

        @Override
        public int read() throws IOException {
            if (bytesRead >= limit) {
                return -1;
            }
            int result = ins.read();
            if (result != -1) {
                bytesRead++;
            }
            return result;
        }

        @Override
        public int read(byte[] b, int off, int len) throws IOException {
            if (bytesRead >= limit) {
                return -1;
            }
            int toRead = Math.min(len, limit - bytesRead);
            int result = ins.read(b, off, toRead);
            if (result != -1) {
                bytesRead += result;
            }
            return result;
        }

        @Override
        public long skip(long n) throws IOException {
            throw new IOException("Skip operation not supported");
        }

        @Override
        public void close() throws IOException {
            ins.close();
        }
    }
}
