                break;
            }
            try {
                Thread.sleep(5000);
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
            }
        }
    }

    private boolean connect() {
        try {
            socketClient = new Socket(hostip, port);
            return true;
        } catch (UnknownHostException e) {
            System.err.println(TAG + ": Unknown host: " + hostip);
        } catch (IOException e) {
            System.err.println(TAG + ": Could not connect to " + hostip + ":" + port);
        }
        return false;
    }

    public void send(byte[] data) {
        handler.post(() -> {
            try {
                OutputStream outputStream = socketClient.getOutputStream();
                outputStream.write(data);
                outputStream.flush();
            } catch (IOException e) {
                System.err.println(TAG + ": Error sending data");
            }
        });
    }

    public void onReceive(ReceiveListener receiveListener) {
        this.receiveListener = receiveListener;
    }

    public void destroy() {
        closeAll();
    }

    private void closeAll() {
        try {
            if (socketClient != null && !socketClient.isClosed()) {
                socketClient.close();
            }
        } catch (IOException e) {
            System.err.println(TAG + ": Error closing socket");
        }
    }

    private void startReceive() {
        receiveThread.start();
    }

    private int getLengthFromHeadByte(byte[] b) {
        return ((b[4] & 0xFF) << 24) | ((b[5] & 0xFF) << 16) | ((b[6] & 0xFF) << 8) | (b[7] & 0xFF);
    }

    private int getTypeFromHeadByte(byte[] b) {
        return ((b[0] & 0xFF) << 24) | ((b[1] & 0xFF) << 16) | ((b[2] & 0xFF) << 8) | (b[3] & 0xFF);
    }

    public interface ReceiveListener {
        void onDataReceived(int type, byte[] data);
    }

    private class Receiver implements Runnable {
        @Override
        public void run() {
            try {
                InputStream inputStream = socketClient.getInputStream();
                byte[] headBuffer = new byte[PACKAGE_HEAD_LENGTH];
                while (inputStream.read(headBuffer) != -1) {
                    int length = getLengthFromHeadByte(headBuffer);
                    byte[] dataBuffer = new byte[length];
                    inputStream.read(dataBuffer);
                    if (receiveListener != null) {
                        receiveListener.onDataReceived(getTypeFromHeadByte(headBuffer), dataBuffer);
                    }
                }
            } catch (IOException e) {
                System.err.println(TAG + ": Error receiving data");
            } finally {
                closeAll();
            }
        }
    }

    private static class Handler {
        private final Object lock = new Object();

        public void post(Runnable runnable) {
            synchronized (lock) {
                new Thread(runnable).start();
            }
        }
    }
