import java.net.Socket;
import java.nio.ByteBuffer;
import java.util.concurrent.atomic.AtomicBoolean;

final class MySQLChannel {
    private final DataSourceConfig dsc;
    private Socket socket;
    private InputStream in;
    private OutputStream out;
    private final AtomicBoolean isClosed = new AtomicBoolean(false);

    public MySQLChannel(DataSourceConfig dsc) {
        this.dsc = dsc;
    }

    public void connect(long timeout) throws IOException {
        socket = new Socket();
        socket.connect(new java.net.InetSocketAddress(dsc.getHost(), dsc.getPort()), (int) timeout);
        socket.setSoTimeout((int) timeout);
        in = new BufferedInputStream(socket.getInputStream());
        out = new BufferedOutputStream(socket.getOutputStream());
    }

    public BinaryPacket execute(String statement) throws IOException {
        CommandPacket command = new CommandPacket(MySQLPacket.COM_QUERY, statement.getBytes());
        sendPacket(command);
        return receive();
    }

    public BinaryPacket receive() throws IOException {
        int packetLength = readInt(in);
        byte[] data = new byte[packetLength];
        readFully(in, data);
        int packetId = readByte(in);
        return new BinaryPacket(data, packetId);
    }

    public void close() throws IOException {
        if (!isClosed.getAndSet(true)) {
            sendPacket(new QuitPacket());
            in.close();
            out.close();
            socket.close();
        }
    }

    private void sendPacket(CommandPacket packet) throws IOException {
        ByteBuffer buffer = ByteBuffer.allocate(packet.getData().length + 5);
        buffer.put((byte) packet.getData().length);
        buffer.put((byte) (packet.getData().length >> 8));
        buffer.put((byte) (packet.getData().length >> 16));
        buffer.put((byte) packet.getPacketId());
        buffer.put(packet.getData());
        out.write(buffer.array());
        out.flush();
    }

    private static int readInt(InputStream in) throws IOException {
        int length = 0;
        length |= readByte(in);
        length |= (readByte(in) << 8);
        length |= (readByte(in) << 16);
        return length;
    }

    private static int readByte(InputStream in) throws IOException {
        int value = in.read();
        if (value == -1) {
            throw new EOFException("Unexpected end of stream");
        }
        return value;
    }

    private static void readFully(InputStream in, byte[] b) throws IOException {
        int len = 0;
        while (len < b.length) {
            int n = in.read(b, len, b.length - len);
            if (n == -1) {
                throw new EOFException("Unexpected end of stream");
            }
            len += n;
        }
    }

    static class BinaryPacket {
        private final byte[] data;
        private final int packetId;

        public BinaryPacket(byte[] data, int packetId) {
            this.data = data;
            this.packetId = packetId;
        }

        // Getters and other methods as needed
    }

    static class CommandPacket {
        private final byte commandType;
        private final byte[] data;
        private final int packetId;

        public CommandPacket(byte commandType, byte[] data) {
            this.commandType = commandType;
            this.data = data;
            this.packetId = 0; // Assuming packet ID starts at 0
        }

        public byte getCommandType() {
            return commandType;
        }

        public byte[] getData() {
            return data;
        }

        public int getPacketId() {
            return packetId;
        }
    }

    static class MySQLPacket {
        public static final byte COM_QUERY = 3;
        // Other constants as needed
    }

    static class QuitPacket extends CommandPacket {
        public QuitPacket() {
            super(MySQLPacket.COM_QUERY, new byte[0]);
        }
    }
}
