class HTTPPacket {
    // Base class implementation for HTTP packets
}

class HTTPResponse {
    private int statusCode;
    private Map<String, String> headers;
    private String body;

    public HTTPResponse(int statusCode, Map<String, String> headers, String body) {
        this.statusCode = statusCode;
        this.headers = headers;
        this.body = body;
    }

    public int getStatusCode() {
        return statusCode;
    }

    public Map<String, String> getHeaders() {
        return headers;
    }

    public String getBody() {
        return body;
    }
}

public class HTTPRequest extends HTTPPacket {
    private Socket postSocket;

    public HTTPResponse post(String host, int port, boolean isKeepAlive) throws IOException {
        postSocket = new Socket(host, port);
        OutputStream out = postSocket.getOutputStream();
        PrintWriter writer = new PrintWriter(out, true);

        String requestLine = "POST / HTTP/1.1\r\n";
        writer.print(requestLine);

        String hostHeader = "Host: " + host + "\r\n";
        writer.print(hostHeader);

        String connectionHeader = "Connection: " + (isKeepAlive ? "keep-alive" : "close") + "\r\n";
        writer.print(connectionHeader);

        String contentTypeHeader = "Content-Type: application/x-www-form-urlencoded\r\n";
        writer.print(contentTypeHeader);

        String contentLengthHeader = "Content-Length: 0\r\n";
        writer.print(contentLengthHeader);

        writer.print("\r\n");

        InputStream in = postSocket.getInputStream();
        BufferedReader reader = new BufferedReader(new InputStreamReader(in));

        StringBuilder responseBuilder = new StringBuilder();
        String line;
        while ((line = reader.readLine()) != null) {
            responseBuilder.append(line).append("\r\n");
        }

        postSocket.close();

        return parseResponse(responseBuilder.toString());
    }

    public HTTPResponse post(String host, int port) throws IOException {
        return post(host, port, false);
    }

    private HTTPResponse parseResponse(String response) {
        String[] lines = response.split("\r\n");
        String statusLine = lines[0];
        int statusCode = Integer.parseInt(statusLine.split(" ")[1]);

        Map<String, String> headers = new HashMap<>();
        int i = 1;
        while (!lines[i].trim().isEmpty()) {
            String[] headerParts = lines[i].split(": ");
            headers.put(headerParts[0], headerParts[1]);
            i++;
        }

        StringBuilder bodyBuilder = new StringBuilder();
        for (int j = i + 1; j < lines.length; j++) {
            bodyBuilder.append(lines[j]).append("\n");
        }

        return new HTTPResponse(statusCode, headers, bodyBuilder.toString());
    }
}
