import java.net.SocketTimeoutException;

public class tcpConnection extends AsyncTask<String, Void, Void> {
    private Activity activity;
    private Context context;
    private static final String TAG = "tcpConnection";
    private static OutputStream out;

    public tcpConnection(Activity activity, Context context) {
        this.activity = activity;
        this.context = context;
    }

    @Override
    protected Void doInBackground(String... strings) {
        String serverAddress = strings[0];
        int serverPort = Integer.parseInt(strings[1]);
        Socket socket = null;
        InputStream in = null;

        while (!isCancelled()) {
            try {
                socket = new Socket();
                socket.connect(new java.net.InetSocketAddress(serverAddress, serverPort), 5000);
                in = socket.getInputStream();
                out = socket.getOutputStream();

                // Send device information as initial message
                String deviceInfo = "Device Info\n";
                out.write(deviceInfo.getBytes());
                out.flush();

                byte[] buffer = new byte[1024];
                int bytesRead;
                while ((bytesRead = in.read(buffer)) != -1) {
                    String response = new String(buffer, 0, bytesRead);
                    if (response.trim().equalsIgnoreCase("exit")) {
                        break;
                    }
                    // Handle incoming commands here
                    Log.d(TAG, "Received: " + response);
                }
            } catch (SocketTimeoutException e) {
                Log.e(TAG, "Connection timed out. Retrying...", e);
            } catch (IOException e) {
                Log.e(TAG, "IO Exception occurred. Retrying...", e);
            } finally {
                try {
                    if (socket != null && !socket.isClosed()) {
                        socket.close();
                    }
                    if (in != null) {
                        in.close();
                    }
                } catch (IOException e) {
                    Log.e(TAG, "Error closing socket or input stream", e);
                }
            }
        }
        return null;
    }
}
