import java.io.IOException;
import java.net.*;
import java.util.HashMap;
import java.util.Map;
import java.util.logging.Logger;

public class NetworkForwarder {
    private static final Logger LOGGER = Logger.getLogger(NetworkForwarder.class.getName());
    private InetAddress destination;
    private DatagramSocket connectionUdp;
    private Map<InetSocketAddress, Socket> connectionsTcp = new HashMap<>();

    public NetworkForwarder(Config config) throws UnknownHostException, SocketException {
        this.destination = InetAddress.getByName(config.getDestinationAddress());
        this.connectionUdp = new DatagramSocket();
    }

    public void forward(InetSocketAddress source, int port, boolean datagram, byte[] data) {
        if (datagram) {
            forwardUdp(data);
        } else {
            forwardTcp(source, port, data);
        }
    }

    private void forwardUdp(byte[] data) {
        try {
            DatagramPacket packet = new DatagramPacket(data, data.length, destination, 0);
            connectionUdp.send(packet);
        } catch (IOException e) {
            LOGGER.warning("Error sending UDP packet: " + e.getMessage());
        }
    }

    private void forwardTcp(InetSocketAddress source, int port, byte[] data) {
        Socket socket = connectionsTcp.get(source);
        if (socket == null) {
            try {
                socket = new Socket(destination.getHostAddress(), port);
                connectionsTcp.put(source, socket);
            } catch (IOException e) {
                LOGGER.warning("Error establishing TCP connection: " + e.getMessage());
                return;
            }
        }
        try {
            socket.getOutputStream().write(data);
        } catch (IOException e) {
            LOGGER.warning("Error sending TCP data: " + e.getMessage());
        }
    }

    public void disconnect(InetSocketAddress source) {
        Socket socket = connectionsTcp.remove(source);
        if (socket != null) {
            try {
                socket.close();
            } catch (IOException e) {
                LOGGER.warning("Error closing TCP connection: " + e.getMessage());
            }
        }
    }
}

class Config {
    private String destinationAddress;

    public Config(String destinationAddress) {
        this.destinationAddress = destinationAddress;
    }

    public String getDestinationAddress() {
        return destinationAddress;
    }
}
