public class MocoSocketHelper implements Closeable {
    private Socket socket;
    private SocketAddress address;
    private PrintStream os;
    private BufferedReader reader;

    public MocoSocketHelper(String target, int port) {
        try {
            this.address = new InetSocketAddress(target, port);
        } catch (IllegalArgumentException e) {
            throw new RuntimeException("Invalid address or port", e);
        }
    }

    public void connect() throws IOException {
        this.socket = new Socket();
        this.socket.connect(address);
        this.os = new PrintStream(socket.getOutputStream(), true);
        this.reader = new BufferedReader(new InputStreamReader(socket.getInputStream()));
    }

    public String send(String request) throws IOException {
        os.println(request);
        return reader.readLine();
    }

    public String send(String request, int readCount) throws IOException {
        os.println(request);
        char[] buffer = new char[readCount];
        int bytesRead = reader.read(buffer, 0, readCount);
        if (bytesRead == -1) {
            return null; // No more data to read
        }
        return new String(buffer, 0, bytesRead);
    }

    @Override
    public void close() throws IOException {
        if (reader != null) reader.close();
        if (os != null) os.close();
        if (socket != null) socket.close();
    }
