public class GreysConsole {
    private Socket socket;
    private BufferedWriter socketWriter;
    private BufferedReader socketReader;
    private boolean isRunning;

    public GreysConsole(InetSocketAddress address) {
        this.isRunning = true;
        try {
            connect(address);
        } catch (IOException e) {
            e.printStackTrace();
            shutdown();
        }
    }

    private Socket connect(InetSocketAddress address) throws IOException {
        this.socket = new Socket();
        this.socket.connect(address, 5000); // Timeout after 5 seconds
        this.socket.setSoTimeout(10000); // Read timeout after 10 seconds
        this.socketWriter = new BufferedWriter(new OutputStreamWriter(socket.getOutputStream()));
        this.socketReader = new BufferedReader(new InputStreamReader(socket.getInputStream()));
        return this.socket;
    }

    public void shutdown() {
        this.isRunning = false;
        try {
            if (socketWriter != null) socketWriter.close();
            if (socketReader != null) socketReader.close();
            if (socket != null) socket.close();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    // Additional methods for sending and receiving data can be added here
}
