    private Receiver receiverThread;
    private RtReceiver receiver;
    private InetAddress host;
    private int port = 7800;
    private boolean server;
    private Log log;
    private List<SocketAddress> members = new ArrayList<>();

    public void receiver(RtReceiver receiver) {
        this.receiver = receiver;
    }

    public Object localAddress() {
        return sock.getLocalSocketAddress();
    }

    public List<? extends Object> clusterMembers() {
        return new ArrayList<>(members);
    }

    public void start(String... options) {
        try {
            if (server) {
                sock = new DatagramSocket(port, host);
            } else {
                sock = new DatagramSocket();
            }
            receiverThread = new Receiver();
            receiverThread.start();
        } catch (IOException e) {
            log.error("Failed to start UDP transport", e);
        }
    }

    public void stop() {
        if (receiverThread != null) {
            receiverThread.interrupt();
        }
        if (sock != null && !sock.isClosed()) {
            sock.close();
        }
    }

    public void send(Object destination, byte[] data, int offset, int length) {
        try {
            DatagramPacket packet = new DatagramPacket(data, offset, length, (SocketAddress) destination);
            sock.send(packet);
        } catch (IOException e) {
            log.error("Failed to send UDP packet", e);
        }
    }

    private class Receiver extends Thread {
        @Override
        public void run() {
            byte[] buffer = new byte[1024];
            DatagramPacket packet = new DatagramPacket(buffer, buffer.length);
            while (!isInterrupted()) {
                try {
                    sock.receive(packet);
                    if (receiver != null) {
                        receiver.onReceive(packet.getData(), packet.getLength(), packet.getSocketAddress());
                    }
                } catch (IOException e) {
                    if (!isInterrupted()) {
                        log.error("Error receiving UDP packet", e);
                    }
                }
            }
        }
    }

    public interface RtReceiver {
        void onReceive(byte[] data, int length, SocketAddress source);
    }

    public interface Log {
        void error(String message, Throwable throwable);
    }
}
