import java.io.IOException;
import java.net.*;
import java.util.Random;

public class SingleClientUdpTracker implements Runnable {
    private static final long CONNECTION_EXPIRATION_TIME = 60000; // 1 minute
    private static final long EPHEMERAL_CONNECTION_ID = 0x41727101980L;
    private static final int CONNECT_CODE = 0;
    private static final int ANNOUNCE_CODE = 1;
    private static final int ERROR_CODE = 3;
    private DatagramSocket serverSocket;
    private int interval;
    private int leechers;
    private int seeders;
    private boolean shutdown = false;
    private long currentConnection;
    private long connectedOn;

    public SingleClientUdpTracker(int interval, int leechers, int seeders) {
        this.interval = interval;
        this.leechers = leechers;
        this.seeders = seeders;
        try {
            serverSocket = new DatagramSocket();
        } catch (SocketException e) {
            e.printStackTrace();
        }
    }

    public SocketAddress getServerAddress() {
        return serverSocket.getLocalSocketAddress();
    }

    @Override
    public void run() {
        byte[] buffer = new byte[1024];
        DatagramPacket requestPacket = new DatagramPacket(buffer, buffer.length);
        while (!shutdown) {
            try {
                serverSocket.receive(requestPacket);
                byte[] responseData = processRequest(requestPacket.getData());
                DatagramPacket responsePacket = new DatagramPacket(responseData, responseData.length, requestPacket.getAddress(), requestPacket.getPort());
                serverSocket.send(responsePacket);
            } catch (IOException e) {
                if (!shutdown) {
                    e.printStackTrace();
                }
            }
        }
        serverSocket.close();
    }

    private byte[] processRequest(byte[] requestData) {
        int action = bytesToInt(requestData, 0);
        int transactionId = bytesToInt(requestData, 4);
        switch (action) {
            case CONNECT_CODE:
                return createConnectResponse(generateConnectionId(), transactionId);
            case ANNOUNCE_CODE:
                if (System.currentTimeMillis() - connectedOn > CONNECTION_EXPIRATION_TIME) {
                    return createError(transactionId, "Connection expired");
                }
                return createAnnounceResponse(currentConnection, transactionId);
            default:
                return createError(transactionId, "Invalid action");
        }
    }

    private long generateConnectionId() {
        Random rand = new Random();
        return EPHEMERAL_CONNECTION_ID + rand.nextInt(10000);
    }

    private byte[] createConnectResponse(long connectionId, int transactionId) {
        byte[] response = new byte[16];
        intToBytes(CONNECT_CODE, response, 0);
        intToBytes(transactionId, response, 4);
        longToBytes(connectionId, response, 8);
        currentConnection = connectionId;
        connectedOn = System.currentTimeMillis();
        return response;
    }

    private byte[] createAnnounceResponse(long connectionId, int transactionId) {
        byte[] response = new byte[20];
        intToBytes(ANNOUNCE_CODE, response, 0);
        intToBytes(transactionId, response, 4);
        intToBytes(interval, response, 8);
        intToBytes(leechers, response, 12);
        intToBytes(seeders, response, 16);
        return response;
    }

    private byte[] createError(int transactionId, String message) {
        byte[] response = new byte[8 + message.getBytes().length];
        intToBytes(ERROR_CODE, response, 0);
        intToBytes(transactionId, response, 4);
        System.arraycopy(message.getBytes(), 0, response, 8, message.getBytes().length);
        return response;
    }

    public void shutdown() {
        shutdown = true;
    }

    private void intToBytes(int value, byte[] buffer, int offset) {
        buffer[offset] = (byte) (value >> 24);
        buffer[offset + 1] = (byte) (value >> 16);
        buffer[offset + 2] = (byte) (value >> 8);
        buffer[offset + 3] = (byte) value;
    }

    private void longToBytes(long value, byte[] buffer, int offset) {
        buffer[offset] = (byte) (value >> 56);
        buffer[offset + 1] = (byte) (value >> 48);
        buffer[offset + 2] = (byte) (value >> 40);
        buffer[offset + 3] = (byte) (value >> 32);
        buffer[offset + 4] = (byte) (value >> 24);
        buffer[offset + 5] = (byte) (value >> 16);
        buffer[offset + 6] = (byte) (value >> 8);
        buffer[offset + 7] = (byte) value;
    }

    private int bytesToInt(byte[] buffer, int offset) {
        return ((buffer[offset] & 0xFF) << 24) |
               ((buffer[offset + 1] & 0xFF) << 16) |
               ((buffer[offset + 2] & 0xFF) << 8) |
               (buffer[offset + 3] & 0xFF);
    }
}
