public class SnmpSocket implements Runnable {
    private DatagramSocket _socket;
    private SnmpResponseHandler _dgramHdlr;
    private Thread _sockThread;
    private byte[] _buffer;
    private boolean isClosing;
    private int _socketPort;
    private int responseBufSize;

    public SnmpSocket(SnmpResponseHandler rspHdlr, InetAddress addr, int bufferSize) {
        try {
            _socket = new DatagramSocket(_socketPort, addr);
            _dgramHdlr = rspHdlr;
            responseBufSize = bufferSize;
            _buffer = new byte[responseBufSize];
            _sockThread = new Thread(this);
            _sockThread.start();
        } catch (IOException e) {
            handleJavaError(e);
        }
    }

    public void sendPacket(byte[] buff, int length, InetAddress addr, int port) {
        DatagramPacket packet = new DatagramPacket(buff, length, addr, port);
        sendPacket(packet);
    }

    public void sendPacket(DatagramPacket dgrmpkt) {
        try {
            _socket.send(dgrmpkt);
        } catch (IOException e) {
            handleJavaError(e);
        }
    }

    public boolean isValid() {
        return !_socket.isClosed() && !_socket.isBound();
    }

    public void close() {
        isClosing = true;
        _socket.close();
        _sockThread.interrupt();
    }

    @Override
    public void run() {
        while (!isClosing) {
            DatagramPacket packet = new DatagramPacket(_buffer, _buffer.length);
            try {
                _socket.receive(packet);
                handleDatagram(packet);
            } catch (IOException e) {
                if (!isClosing) {
                    handleJavaError(e);
                }
            }
        }
    }

    protected void finalize() throws Throwable {
        try {
            close();
        } finally {
            super.finalize();
        }
    }

