import java.io.InputStreamReader;
import java.io.OutputStream;
import java.net.HttpURLConnection;
import java.net.URL;
import java.util.Map;

public final class HttpURLConnectionUtils {

    public static final int TIMEOUT_IN_MILLIS = 5000;

    public interface Callback {
        void onResponse(String result, long responseTime);
        void onFail(Throwable error);
    }

    public static void doGetAsync(String urlStr, Callback callback) {
        request("GET", urlStr, null, null, callback);
    }

    public static void doPostAsync(String urlStr, String params, Callback callback) {
        request("POST", urlStr, null, params, callback);
    }

    private static void request(final String method, final String urlStr, final Map<String, String> headers, final String params, final Callback callback) {
        new Thread(() -> {
            HttpURLConnection connection = null;
            try {
                URL url = new URL(urlStr);
                connection = (HttpURLConnection) url.openConnection();
                connection.setRequestMethod(method);
                connection.setConnectTimeout(TIMEOUT_IN_MILLIS);
                connection.setReadTimeout(TIMEOUT_IN_MILLIS);

                if (headers != null) {
                    for (Map.Entry<String, String> entry : headers.entrySet()) {
                        connection.setRequestProperty(entry.getKey(), entry.getValue());
                    }
                }

                if ("POST".equals(method) && params != null) {
                    connection.setDoOutput(true);
                    try (OutputStream os = connection.getOutputStream()) {
                        byte[] input = params.getBytes("utf-8");
                        os.write(input, 0, input.length);
                    }
                }

                long startTime = System.currentTimeMillis();
                int responseCode = connection.getResponseCode();
                long responseTime = System.currentTimeMillis() - startTime;

                if (responseCode == HttpURLConnection.HTTP_OK) {
                    StringBuilder response = new StringBuilder();
                    try (BufferedReader br = new BufferedReader(new InputStreamReader(connection.getInputStream(), "utf-8"))) {
                        String responseLine;
                        while ((responseLine = br.readLine()) != null) {
                            response.append(responseLine.trim());
                        }
                    }
                    callback.onResponse(response.toString(), responseTime);
                } else {
                    callback.onFail(new RuntimeException("Failed : HTTP error code : " + responseCode));
                }
            } catch (Exception e) {
                callback.onFail(e);
            } finally {
                if (connection != null) {
                    connection.disconnect();
                }
            }
        }).start();
    }
}
