import android.content.Context;
import android.util.Log;
import java.io.IOException;
import java.net.HttpURLConnection;
import java.net.URL;

public class HttpStatusChecker {
    private Context context;
    private String url;

    public HttpStatusChecker(Context context, String url) {
        this.context = context;
        this.url = url;
    }

    public StatusResult checkStatus() {
        HttpURLConnection connection = null;
        try {
            URL u = new URL(url);
            connection = (HttpURLConnection) u.openConnection();
            connection.setRequestMethod("GET");
            connection.setInstanceFollowRedirects(false);
            int responseCode = connection.getResponseCode();

            String message = getStatusMessage(responseCode);
            String redirectionUrl = null;

            if (isRedirection(responseCode)) {
                redirectionUrl = connection.getHeaderField("Location");
                message += " Redirecting to: " + redirectionUrl;
            }

            return new StatusResult(message, redirectionUrl);

        } catch (IOException e) {
            Log.e("HttpStatusChecker", "Error checking status: " + e.getMessage());
            return new StatusResult("Error checking status: " + e.getMessage(), null);
        } finally {
            if (connection != null) {
                connection.disconnect();
            }
        }
    }

    private boolean isRedirection(int responseCode) {
        return responseCode >= 300 && responseCode < 400;
    }

    private String getStatusMessage(int responseCode) {
        switch (responseCode) {
            case 200:
                return "OK";
            case 301:
                return "Moved Permanently";
            case 302:
                return "Found";
            case 303:
                return "See Other";
            case 307:
                return "Temporary Redirect";
            case 308:
                return "Permanent Redirect";
            case 400:
                return "Bad Request";
            case 401:
                return "Unauthorized";
            case 403:
                return "Forbidden";
            case 404:
                return "Not Found";
            case 500:
                return "Internal Server Error";
            case 502:
                return "Bad Gateway";
            case 503:
                return "Service Unavailable";
            default:
                return "HTTP " + responseCode;
        }
    }

    public static class StatusResult {
        private String message;
        private String redirectionUrl;

        public StatusResult(String message, String redirectionUrl) {
            this.message = message;
            this.redirectionUrl = redirectionUrl;
        }

        public String getMessage() {
            return message;
        }

        public String getRedirectionUrl() {
            return redirectionUrl;
        }
    }
}
