        } catch (IOException e) {
            throw new RuntimeException(e);
        }
    }

    private byte[] getBytes(long start, int length) throws IOException {
        ByteBuffer buffer = ByteBuffer.allocate(length);
        channel.position(start);
        while (buffer.hasRemaining()) {
            channel.read(buffer);
        }
        return buffer.array();
    }

    private List<Pmtiles.Entry> readDir(long offset, int length) throws IOException {
        ByteBuffer buffer = ByteBuffer.allocate(length);
        channel.position(offset);
        while (buffer.hasRemaining()) {
            channel.read(buffer);
        }
        return Pmtiles.parseDirectoryEntries(buffer);
    }

    private Stream<Tile> getTiles(List<Pmtiles.Entry> dir) {
        return dir.stream()
                .map(entry -> new Tile(entry.x, entry.y, entry.z, getBytes(entry.offset, entry.length)));
    }
