        this.writeLock = ThreadPool.readThreadPool().newWriteLock("translog_writer");
    }

    @Override
    public void writeAndReleaseOps(ReleasableBytesReference ops) throws IOException {
        try {
            writeLock.acquire();
            writeToFile(ops.toBytesRef().toByteBuffer());
            ops.close();
        } finally {
            writeLock.release();
        }
    }

    private void writeToFile(ByteBuffer buffer) throws IOException {
        synchronized (syncLock) {
            channel.position(totalOffset);
            channel.write(buffer);
            totalOffset += buffer.remaining();
        }
    }

    private void readBytes(ByteBuffer target, long position) throws IOException {
        synchronized (syncLock) {
            channel.position(position);
            channel.read(target);
        }
    }

    public static void writeCheckpoint(FileChannel channel, Path path, Checkpoint checkpoint) throws IOException {
        try (ReleasableBytesStreamOutput out = new ReleasableBytesStreamOutput()) {
            out.writeLong(checkpoint.translogUUID());
            out.writeLong(checkpoint.globalCheckpoint());
            out.writeLong(checkpoint.minTranslogGeneration());
            out.writeLong(checkpoint.maxSeqNo());
            out.writeLong(checkpoint.maxUnsafeAutoIdTimestamp());
            out.writeLong(checkpoint.primaryTerm());
            out.writeLong(checkpoint.formatVersion());
            out.writeLong(checkpoint.checkpointId());
            channel.position(path.getFileName().toString().length() + 1);
            channel.write(out.bytes().toBytesRef().toByteBuffer());
        }
    }

    @Override
    public void close() throws IOException {
        if (closed.compareAndSet(false, true)) {
            writeLock.acquire();
            try {
                buffer.close();
                channel.close();
                checkpointChannel.close();
            } finally {
                writeLock.release();
            }
        }
    }
}
