        this.clientResources = clientResources.mutate()
                .socketAddressResolver(MappingSocketAddressResolver.create(DnsResolver.jvmDefault(), hostAndPort -> {
                    return HostAndPort.of(hostAndPort.getHostText(), hostAndPort.getPort() + 443);
                })).build();
    }

    public StatefulRedisMasterReplicaConnection<String, String> createConnection(String host, String masterId) {
        RedisClient client = RedisClient.create(clientResources);
        RedisURI redisURI = RedisURI.create("rediss-sentinel://" + host + ":26379?sentinelMasterId=" + masterId);
        redisURI.setVerifyPeer(false);

        StatefulRedisMasterReplicaConnection<String, String> connection = 
            MasterReplica.connect(client, StringCodec.UTF8, redisURI);
        connection.setReadFrom(ReadFrom.REPLICA);

        return connection;
    }

    public void closeConnection(StatefulRedisMasterReplicaConnection<String, String> connection) {
        if (connection != null) {
            connection.close();
            FastShutdown.shutdown(connection.getRedisClient());
        }
    }

    public void performOperations(StatefulRedisMasterReplicaConnection<String, String> connection, String key, String value) {
        connection.sync().set(key, value);
        connection.sync().get(key);
    }
}

