enum Hashing {
    MURMUR_HASH,
    CRC32,
    SHA1;

    public long hash(byte[] data) {
        switch (this) {
            case MURMUR_HASH:
                return MurmurHash.murmurhash3_x86_32(data, 0, data.length, 0);
            case CRC32:
                return crc32(data);
            case SHA1:
                return sha1(data);
            default:
                throw new UnsupportedOperationException("Unsupported hashing algorithm");
        }
    }

    private static long murmurhash3_x86_32(byte[] key, int offset, int length, int seed) {
        int c1 = 0xcc9e2d51;
        int c2 = 0x1b873593;
        int h1 = seed;
        int roundedEnd = offset + (length & 0xfffffffc); // round down to 4 byte block
        for (int i = offset; i < roundedEnd; i += 4) {
            int k1 = ((key[i] & 0xff)) | ((key[i + 1] & 0xff) << 8) | ((key[i + 2] & 0xff) << 16) | ((key[i + 3] & 0xff) << 24);
            k1 *= c1;
            k1 = Integer.rotateLeft(k1, 15);
            k1 *= c2;
            h1 ^= k1;
            h1 = Integer.rotateLeft(h1, 13);
            h1 = h1 * 5 + 0xe6546b64;
        }
        int k1 = 0;
        switch (length & 0b11) {
            case 3:
                k1 ^= (key[roundedEnd + 2] & 0xff) << 16;
            case 2:
                k1 ^= (key[roundedEnd + 1] & 0xff) << 8;
            case 1:
                k1 ^= key[roundedEnd] & 0xff;
                k1 *= c1;
                k1 = Integer.rotateLeft(k1, 15);
                k1 *= c2;
                h1 ^= k1;
        }
        h1 ^= length;
        h1 ^= h1 >>> 16;
        h1 *= 0x85ebca6b;
        h1 ^= h1 >>> 13;
        h1 *= 0xc2b2ae35;
        h1 ^= h1 >>> 16;
        return h1;
    }

    private static long crc32(byte[] bytes) {
        CRC32 crc32 = new CRC32();
        crc32.update(bytes);
        return crc32.getValue();
    }

    private static long sha1(byte[] bytes) {
        MessageDigest md = null;
        try {
            md = MessageDigest.getInstance("SHA-1");
        } catch (NoSuchAlgorithmException e) {
            throw new RuntimeException(e);
        }
        byte[] digest = md.digest(bytes);
        return (((long) digest[0]) & 0xFFL) |
               (((long) digest[1]) & 0xFFL) << 8 |
