import org.apache.rocketmq.client.consumer.DefaultMQPushConsumer;
import org.apache.rocketmq.client.consumer.listener.ConsumeConcurrentlyContext;
import org.apache.rocketmq.client.consumer.listener.ConsumeConcurrentlyStatus;
import org.apache.rocketmq.client.consumer.listener.MessageListenerConcurrently;
import org.apache.rocketmq.client.exception.MQClientException;
import org.apache.rocketmq.common.MixAll;
import org.apache.rocketmq.common.message.MessageExt;
import org.apache.rocketmq.common.topic.TopicValidator;
import org.apache.rocketmq.remoting.RPCHook;

public class RocketMQConsumer {

    private final DefaultMQPushConsumer defaultMQPushConsumer;

    public RocketMQConsumer(RPCHook rpcHook, String namesrvAddr) throws MQClientException {
        this.defaultMQPushConsumer = new DefaultMQPushConsumer(MixAll.MONITOR_CONSUMER_GROUP, rpcHook);
        this.defaultMQPushConsumer.setNamesrvAddr(namesrvAddr);
        this.defaultMQPushConsumer.setInstanceName(generateInstanceName(namesrvAddr));
        this.defaultMQPushConsumer.setConsumeThreadMin(1);
        this.defaultMQPushConsumer.setConsumeThreadMax(1);

        configureMessageListener();
        subscribeToTopic();
    }

    private String generateInstanceName(String namesrvAddr) {
        return "MonitorService_" + (System.currentTimeMillis() + RandomUtils.nextInt(0, Integer.MAX_VALUE) + namesrvAddr).hashCode();
    }

    private void configureMessageListener() {
        this.defaultMQPushConsumer.registerMessageListener(new MessageListenerConcurrently() {
            @Override
            public ConsumeConcurrentlyStatus consumeMessage(List<MessageExt> msgs,
                ConsumeConcurrentlyContext context) {
                try {
                    OffsetMovedEvent ome =
                        OffsetMovedEvent.decode(msgs.get(0).getBody(), OffsetMovedEvent.class);

                    DeleteMsgsEvent deleteMsgsEvent = new DeleteMsgsEvent();
                    deleteMsgsEvent.setOffsetMovedEvent(ome);
                    deleteMsgsEvent.setEventTimestamp(msgs.get(0).getStoreTimestamp());

                    MonitorService.this.monitorListener.reportDeleteMsgsEvent(deleteMsgsEvent);
                } catch (Exception e) {
                }

                return ConsumeConcurrentlyStatus.CONSUME_SUCCESS;
            }
        });
    }

    private void subscribeToTopic() throws MQClientException {
        this.defaultMQPushConsumer.subscribe(TopicValidator.RMQ_SYS_OFFSET_MOVED_EVENT, "*");
    }

    public void start() throws MQClientException {
        this.defaultMQPushConsumer.start();
    }

    public void shutdown() {
        this.defaultMQPushConsumer.shutdown();
    }
}

