    public static MappedByteBuffer mapNewOrExistingMarkFile(File markFile, boolean createIfAbsent, int versionFieldOffset, int timestampFieldOffset, int versionLength, long defaultTimestamp, EpochClock epochClock, IntConsumer errorConsumer, Consumer<String> warningConsumer) throws IOException {
        Path path = Paths.get(markFile.getAbsolutePath());
        if (!Files.exists(path)) {
            if (createIfAbsent) {
                Files.createFile(path);
                writeVersionAndTimestamp(markFile, versionFieldOffset, timestampFieldOffset, versionLength, defaultTimestamp, epochClock);
            } else {
                throw new FileNotFoundException("Mark file does not exist");
            }
        } else {
            validateVersionAndTimestamp(markFile, versionFieldOffset, timestampFieldOffset, versionLength, epochClock, errorConsumer, warningConsumer);
        }
        return FileChannel.open(path, StandardOpenOption.READ, StandardOpenOption.WRITE).map(FileChannel.MapMode.READ_WRITE, 0, markFile.length());
    }

    public static void ensureMarkFileLink(File markFile, File linkDir, String linkName) throws IOException {
        Path linkPath = Paths.get(linkDir.getAbsolutePath(), linkName);
        if (!Files.exists(linkPath)) {
            Files.createSymbolicLink(linkPath, Paths.get(markFile.getParentFile().getAbsolutePath()));
        }
    }

    private static void writeVersionAndTimestamp(File markFile, int versionFieldOffset, int timestampFieldOffset, int versionLength, long timestamp, EpochClock epochClock) throws IOException {
        try (RandomAccessFile raf = new RandomAccessFile(markFile, "rw")) {
            byte[] versionBytes = new byte[versionLength];
            System.arraycopy(("v" + epochClock.version()).getBytes(), 0, versionBytes, 0, versionLength);
            raf.seek(versionFieldOffset);
            raf.write(versionBytes);
            raf.seek(timestampFieldOffset);
            raf.writeLong(timestamp);
        }
    }

    private static void validateVersionAndTimestamp(File markFile, int versionFieldOffset, int timestampFieldOffset, int versionLength, EpochClock epochClock, IntConsumer errorConsumer, Consumer<String> warningConsumer) throws IOException {
        try (RandomAccessFile raf = new RandomAccessFile(markFile, "r")) {
            byte[] versionBytes = new byte[versionLength];
            raf.seek(versionFieldOffset);
            raf.readFully(versionBytes);
            String versionStr = new String(versionBytes).trim();
            if (!versionStr.startsWith("v") || !epochClock.isCompatible(Integer.parseInt(versionStr.substring(1)))) {
                errorConsumer.accept(2);
            }
            raf.seek(timestampFieldOffset);
            long recordedTimestamp = raf.readLong();
            if (recordedTimestamp < epochClock.lastKnownGoodTimestamp()) {
                warningConsumer.accept("Outdated timestamp detected");
            }
        }
    }

