public class OpenMHzHttpClient {

    private final static Logger mLog = LoggerFactory.getLogger(OpenMHzHttpClient.class);
    private HttpClient mHttpClient = HttpClient.newBuilder()
        .version(HttpClient.Version.HTTP_2)
        .followRedirects(HttpClient.Redirect.NORMAL)
        .connectTimeout(Duration.ofSeconds(20))
        .build();

    public String testConnection(String host, String systemName, String apiKey) {
        String uri = host + "/" + systemName + "/authorize";

        HttpRequest request = HttpRequest.newBuilder()
            .uri(URI.create(uri))
            .header(HttpHeaders.CONTENT_TYPE, "application/x-www-form-urlencoded")
            .header(HttpHeaders.USER_AGENT, "sdrtrunk")
            .POST(HttpRequest.BodyPublishers.ofString("api_key=" + apiKey))
            .build();

        try {
            HttpResponse<String> response = mHttpClient.send(request, HttpResponse.BodyHandlers.ofString());

            if (response.statusCode() == 200) {
                return "OK";
            } else if(response.statusCode() == 403) {
                return "Invalid API Key";
            } else if(response.statusCode() == 500) {
                return "Invalid System Name";
            }

            return "No Response";
        }
        catch(Exception e) {  
            Throwable throwableCause = e.getCause();

            if(throwableCause instanceof ConnectException) {
                return "No Response";
            }

            mLog.error("Exception connecting to OpenMHz server [" + e.toString() + "]");
            return "Unknown Exception";
        }
    }

