import io.vertx.core.buffer.Buffer;
import io.vertx.core.net.NetClient;
import io.vertx.core.net.NetServer;

public class UDPServer extends AbstractVerticle {

  @Override
  public void start() {
    NetServer server = vertx.createNetServer();
    server.connectHandler(socket -> {
      socket.handler(buffer -> {
        System.out.println("Received data: " + buffer.toString());
      });
    }).listen(12345);
  }

  public static void main(String[] args) {
    Vertx vertx = Vertx.vertx();
    vertx.deployVerticle(new UDPServer());

    // Create a UDP client
    NetClient client = vertx.createNetClient();
    client.connect(12345, "localhost", connectResult -> {
      if (connectResult.succeeded()) {
        NetSocket socket = connectResult.result();

        // Send a Buffer
        Buffer buffer = Buffer.buffer("Hello, Buffer!");
        socket.write(buffer, writeResult -> {
          if (writeResult.succeeded()) {
            System.out.println("Buffer sent successfully");
          } else {
            System.err.println("Failed to send Buffer: " + writeResult.cause().getMessage());
          }
        });

        // Send a String
        String message = "Hello, String!";
        socket.write(message, writeResult -> {
          if (writeResult.succeeded()) {
            System.out.println("String sent successfully");
          } else {
            System.err.println("Failed to send String: " + writeResult.cause().getMessage());
          }
        });
      } else {
        System.err.println("Failed to connect: " + connectResult.cause().getMessage());
      }
    });
  }
}
