            try {
                session.close();
            } catch (IOException e) {
                logger.error("Error closing session", e);
            }
        }
    }

    private void broadcastMessage(Session excludeSession, String message) {
        for (Map.Entry<String, Session> entry : ONLINE_USER_SESSIONS.entrySet()) {
            Session session = entry.getValue();
            if (session.isOpen() && !session.equals(excludeSession)) {
                try {
                    session.getBasicRemote().sendText(message);
                } catch (IOException e) {
                    logger.error("Error sending message to user " + entry.getKey(), e);
                }
            }
        }
    }
}
