import java.net.ServerSocket;
import java.net.Socket;
import java.io.IOException;
import java.io.OutputStream;
import java.util.Scanner;

public class WebSocketServer {

    private ServerSocket serverSocket;
    private int port;
    private Thread serverThread;

    public WebSocketServer(int port) {
        this.port = port;
    }

    public void start() {
        serverThread = new Thread(() -> {
            try {
                serverSocket = new ServerSocket(port);
                serverSocket.setReuseAddress(true);
                while (true) {
                    Socket client = null;
                    try {
                        client = serverSocket.accept();
                        handleClientConnection(client);
                    } catch (IOException e) {
                        // Handle client connection error
                    }
                }
            } catch (Exception e) {
                e.printStackTrace();
            }
        });
        serverThread.start();
    }

    private void handleClientConnection(Socket client) throws IOException {
        Scanner in = new Scanner(client.getInputStream());
        OutputStream os = client.getOutputStream();

        // Read client handshake
        String input = in.nextLine();
        String testCase = input.split(" ")[1];
        String seckey = "";
        while (in.hasNext()) {
            input = in.nextLine();
            if (input.startsWith("Sec-WebSocket-Key: ")) {
                seckey = input.split(" ")[1];
            }
            if (input.startsWith("Upgrade")) {
                break;
            }
        }

        // Send response based on test case
        String response = "HTTP/1.1 101 Websocket Connection Upgrade\r\n" +
                         "Upgrade: websocket\r\n" +
                         "Connection: Upgrade\r\n" +
                         "Sec-WebSocket-Accept: " + generateFinalKey(seckey) + "\r\n\r\n";
        os.write(response.getBytes());
        os.flush();
    }

    public void stop() throws IOException {
        if (serverSocket != null) {
            serverSocket.close();
        }
        if (serverThread != null) {
            serverThread.interrupt();
        }
    }

    private String generateFinalKey(String in) {
        String seckey = in.trim();
        String acc = seckey + "258EAFA5-E914-47DA-95CA-C5AB0DC85B11";
        try {
            java.security.MessageDigest sh1 = java.security.MessageDigest.getInstance("SHA1");
            return org.java_websocket.util.Base64.encodeBytes(sh1.digest(acc.getBytes()));
        } catch (java.security.NoSuchAlgorithmException e) {
            throw new IllegalStateException(e);
        }
    }
}

