                        e.printStackTrace();
                    }
                }
            });
            serverThread.start();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public void stop() {
        try {
            if (serverSocket != null && !serverSocket.isClosed()) {
                serverSocket.close();
            }
            if (serverThread != null && serverThread.isAlive()) {
                serverThread.interrupt();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    private void handleClientConnection(Socket client) {
        try (
            InputStream input = client.getInputStream();
            OutputStream output = client.getOutputStream()
        ) {
            BufferedReader reader = new BufferedReader(new InputStreamReader(input));
            PrintWriter writer = new PrintWriter(output, true);

            // Read HTTP request
            StringBuilder httpRequest = new StringBuilder();
            String line;
            while ((line = reader.readLine()) != null && !line.isEmpty()) {
                httpRequest.append(line).append("\r\n");
            }

            // Extract Sec-WebSocket-Key from request
            String[] lines = httpRequest.toString().split("\n");
            String secWebSocketKey = null;
            for (String l : lines) {
                if (l.startsWith("Sec-WebSocket-Key: ")) {
                    secWebSocketKey = l.substring(22).trim();
                    break;
                }
            }

            if (secWebSocketKey == null) {
                writer.println("HTTP/1.1 400 Bad Request");
                return;
            }

            // Generate Sec-WebSocket-Accept key
            String finalKey = generateFinalKey(secWebSocketKey);

            // Send WebSocket handshake response
            writer.println("HTTP/1.1 101 Switching Protocols");
            writer.println("Upgrade: websocket");
            writer.println("Connection: Upgrade");
            writer.println("Sec-WebSocket-Accept: " + finalKey);

            // Client is now connected and ready for WebSocket messages
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    private String generateFinalKey(String in) {
        String guid = "258EAFA5-E914-47DA-95CA-C5AB0DC85B11";
        String combined = in + guid;
        MessageDigest md = null;
        try {
            md = MessageDigest.getInstance("SHA-1");
        } catch (NoSuchAlgorithmException e) {
            e.printStackTrace();
        }
        byte[] hash = md.digest(combined.getBytes());
        Base64.Encoder encoder = Base64.getEncoder();
        return encoder.encodeToString(hash);
    }
}
