        socket.connect(new InetSocketAddress(host, port));
        inputStream = socket.getInputStream();
        outputStream = socket.getOutputStream();
    }

    public void sendData(InputStream source) throws IOException {
        byte[] dataArray = new byte[1024];
        int length;
        do {
            length = source.read(dataArray);
            if (length <= 0) {
                break;
            }
            outputStream.write(dataArray, 0, length);
            outputStream.flush();
        } while (length > 0);
    }

    public void receiveData(OutputStream destination) throws IOException {
        byte[] dataArray = new byte[1024];
        int length;
        do {
            length = inputStream.read(dataArray);
            if (length == 1 && dataArray[0] == 0x04) {
                destination.flush();
                break;
            }
            if (length <= 0) {
                break;
            }
            destination.write(dataArray, 0, length);
        } while (length > 0);
    }

    public void close() {
        IOUtils.closeQuietly(inputStream);
        IOUtils.closeQuietly(outputStream);
        try {
            if (socket != null) {
                socket.close();
            }
        } catch (IOException e) {
            // ignore
        }
    }

