import java.net.Socket;
import java.util.concurrent.atomic.AtomicBoolean;

public final class MySQLChannel {

    private final DataSourceConfig dsc;
    private Socket socket;
    private InputStream in;
    private OutputStream out;
    private final AtomicBoolean isClosed;

    public MySQLChannel(DataSourceConfig dsc) {
        this.dsc = dsc;
        this.isClosed = new AtomicBoolean(false);
    }

    public void connect(long timeout) throws Exception {
        socket = new Socket();
        socket.setTcpNoDelay(true);
        socket.setTrafficClass(0x04 | 0x10);
        socket.setPerformancePreferences(0, 2, 1);
        socket.setReceiveBufferSize(16 * 1024);
        socket.setSendBufferSize(8 * 1024);
        socket.connect(new InetSocketAddress(dsc.getHost(), dsc.getPort()), 10 * 1000);

        in = new BufferedInputStream(socket.getInputStream(), 16 * 1024);
        out = new BufferedOutputStream(socket.getOutputStream(), 8 * 1024);
    }

    public BinaryPacket execute(String statement) throws IOException {
        CommandPacket packet = new CommandPacket();
        packet.packetId = 0;
        packet.command = MySQLPacket.COM_QUERY;
        packet.arg = statement.getBytes();

        packet.write(out);
        out.flush();
        return receive();
    }

    public BinaryPacket receive() throws IOException {
        BinaryPacket bin = new BinaryPacket();
        bin.read(in);
        return bin;
    }

    @Override
    public void close() {
        if (isClosed.compareAndSet(false, true)) {
            try {
                if (out != null) {
                    out.write(QuitPacket.QUIT);
                    out.flush();
                }
            } catch (IOException e) {
                // Log error if needed
            } finally {
                try {
                    socket.close();
                } catch (Throwable e) {
                    // Log error if needed
                }
            }
        }
    }

    private static class BinaryPacket {
        public byte[] data;
        public int packetId;

        public void read(InputStream in) throws IOException {
            // Implementation of packet reading
        }
    }

    private static class CommandPacket {
