                try {
                    if (out != null) out.close();
                } catch (Exception e) {};
                try {
                    if (postSocket != null) postSocket.close();
                } catch (Exception e) {};
                postSocket = null;
            }
        }

        return httpRes;
    }

    public HTTPResponse post(String host, int port) {
        return post(host, port, false);
    }
