import java.net.Proxy;
import java.net.URL;

public class HttpRequestManager {
    private boolean mProxyOn;
    private String mProxyHost;
    private long mProxyPort;

    public HttpRequestManager(boolean proxyOn, String proxyHost, long proxyPort) {
        this.mProxyOn = proxyOn;
        this.mProxyHost = proxyHost;
        this.mProxyPort = proxyPort;
    }

    public void requestSubscribeServerConfigs(String urlStr, Callback callback) {
        try {
            URL url = new URL(urlStr);
            HttpURLConnection connection = (HttpURLConnection) url.openConnection();
            if (mProxyOn) {
                Proxy proxy = new Proxy(Proxy.Type.HTTP, new InetSocketAddress(mProxyHost, mProxyPort));
                connection.setProxy(proxy);
            }
            connection.setRequestMethod("GET");
            int responseCode = connection.getResponseCode();
            if (responseCode == HttpURLConnection.HTTP_OK) {
                InputStream inputStream = connection.getInputStream();
                String response = readStringFromStream(inputStream);
                callback.onSuccess(response);
            } else {
                callback.onFailed();
            }
        } catch (Exception e) {
            callback.onFailed();
        }
    }

    private String readStringFromStream(InputStream inputStream) throws Exception {
        StringBuilder stringBuilder = new StringBuilder();
        byte[] buffer = new byte[1024];
        int bytesRead;
        while ((bytesRead = inputStream.read(buffer)) != -1) {
            stringBuilder.append(new String(buffer, 0, bytesRead));
        }
        return stringBuilder.toString();
    }

    public interface Callback {
        void onSuccess(String response);
        void onFailed();
    }
}
